<script>
export default {
  name: 'Import',
  components: {},
  computed: {
    urlHash() {
      return this.$route.hash.replace('#', '');
    },
    urlData() {
      try {
        return JSON.parse(atob(this.urlHash));
      } catch (e) {
        return false;
      }
    },
    answerData() {
      const answers = this.urlData.answers;

      const sozialarbeitername = answers['startseite-sozialarbeitername'].value;
      const angehoerigenname = answers['startseite-angehoerigenname'].value;
      const datum = answers['startseite-datum'].value;
      const formatiertesDatum = new Date(datum).toLocaleDateString('de-AT', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit'
      });

      return {
        SozialarbeiterIn: sozialarbeitername,
        Angehörige: angehoerigenname,
        Datum: formatiertesDatum
      };
    }
  },
  methods: {
    importData() {
      this.$store.commit('importState', this.urlData);
      this.$router.push('/auswertung');
    }
  }
};
</script>

